import React, { useState } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import './App.css';

const Ashu = () => {
  const [image, setImage] = useState(null);
  const [ingredients, setIngredients] = useState([]);
  const [probabilities, setProbabilities] = useState([]);
  const [mass, setMass] = useState(100);
  const [nutritionInfo, setNutritionInfo] = useState(null);
  const [capturing, setCapturing] = useState(false);

  const webcamRef = React.useRef(null);

  const captureImage = () => {
    setCapturing(true);
    const imageSrc = webcamRef.current.getScreenshot();
    setImage(imageSrc);  // Storing the captured image as base64 string
  };

  const handleMassChange = (e) => {
    setMass(e.target.value);
  };

  // Handle base64 to send to backend
  const predictIngredients = async () => {
    try {
      const response = await axios.post('http://localhost:5001/predict_image', {
        image: image,  // Send the base64 image directly to the backend
      });
      
      setIngredients(response.data.ingredients);
      setProbabilities(response.data.probabilities);
    } catch (error) {
      console.error("Error in image prediction:", error);
    }
  };

  const predictNutrition = async () => {
    try {
      const response = await axios.post('http://localhost:5001/predict_portion', {
        image: image,  // Send the base64 image directly to the backend
        mass: mass
      });
      
      setNutritionInfo(response.data);
    } catch (error) {
      console.error("Error in portion prediction:", error);
    }
  };

  return (
    <div className="App">
      <h1>Food Nutrient Prediction</h1>

      {capturing ? (
        <>
          <img src={image} alt="Captured" />
          <div>
            <label>Mass (grams):</label>
            <input 
              type="number" 
              value={mass} 
              onChange={handleMassChange} 
            />
            <button onClick={predictNutrition}>Predict Nutrition</button>
            <div>
              {nutritionInfo && (
                <div>
                  <h3>Nutrition Info:</h3>
                  <p>Protein: {nutritionInfo.protein}</p>
                  <p>Fat: {nutritionInfo.fat}</p>
                  <p>Carbs: {nutritionInfo.carbs}</p>
                  <p>Calories: {nutritionInfo.calories}</p>
                </div>
              )}
            </div>
          </div>
        </>
      ) : (
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          width="100%"
        />
      )}
      
      <button onClick={captureImage}>Capture Image</button>

      <div>
        <h3>Predicted Ingredients:</h3>
        <ul>
          {ingredients.map((ingredient, index) => (
            <li key={index}>{ingredient}: {probabilities[index]}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Ashu;
