import React, { useState, useEffect } from 'react';
import './log.css';

const LogFoodPage = () => {
    const [foodData, setFoodData] = useState({});
    const [suggestions, setSuggestions] = useState([]);
    const [foodInput, setFoodInput] = useState('');
    const [quantity, setQuantity] = useState(100);
    const [unit, setUnit] = useState(1);
    const [popupVisible, setPopupVisible] = useState(false);
    const [totalCalories, setTotalCalories] = useState(0);
    const [recentlyAddedFood, setRecentlyAddedFood] = useState([]);

    useEffect(() => {
        // Fetch the user's total calorie intake when the page loads
        fetch('http://localhost:5001/user-calories')
            .then(response => response.json())
            .then(data => setTotalCalories(data.totalCalories));
    }, []);

    const handleSearchChange = async (e) => {
        const query = e.target.value;
        setFoodInput(query);

        if (!query) {
            setSuggestions([]);
            return;
        }

        // Fetch suggestions based on the search term
        const response = await fetch(`http://localhost:5001/search?query=${query}`);
        const data = await response.json();
        setSuggestions(data);
    };

    const getProduct = async (code) => {
        const unit = parseFloat(document.getElementById('unit').value);
        let quantity = parseFloat(document.getElementById('quantity').value);
        if (isNaN(quantity) || quantity <= 0) quantity = 100;

        // Fetch product nutritional information
        const response = await fetch(`http://localhost:5001/product/${code}`);
        const data = await response.json();
        
        setFoodData(data);
        setPopupVisible(true);
    };

    const saveFood = async () => {
        const response = await fetch('http://localhost:5001/save', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                name: foodData.name,
                calories: foodData.calories * (quantity / 100),
                carbs: foodData.carbs * (quantity / 100),
                proteins: foodData.proteins * (quantity / 100),
                fats: foodData.fats * (quantity / 100),
            }),
        });

        if (response.ok) {
            alert('Food saved successfully');
            // Update total calories
            fetch('http://localhost:5001/user-calories')
                .then(response => response.json())
                .then(data => setTotalCalories(data.totalCalories));

            setPopupVisible(false);
            updateRecentlyAddedFood(foodData.name);
        }
    };

    const updateRecentlyAddedFood = (foodName) => {
        setRecentlyAddedFood(prevFoods => [foodName, ...prevFoods].slice(0, 3)); // Show top 3 recent foods
    };

    return (
        <div className="log-food-page">
            <h1>Log Your Food</h1>

            <div className="calories-tracker">
                <h2>Total Calorie Intake: {totalCalories} / 2415 cal</h2>
            </div>

            <div className="search-container">
                <input
                    type="text"
                    value={foodInput}
                    onChange={handleSearchChange}
                    placeholder="Search food"
                />
                <input
                    type="number"
                    value={quantity}
                    onChange={(e) => setQuantity(e.target.value)}
                    placeholder="Quantity"
                />
                <select onChange={(e) => setUnit(e.target.value)} value={unit}>
                    <option value={1}>gm</option>
                    <option value={0.001}>mg</option>
                    <option value={1000}>kg</option>
                </select>
            </div>

            <div id="suggestions">
                {suggestions.map((product) => (
                    <div
                        key={product.code}
                        onClick={() => getProduct(product.code)} // Get product when clicked
                    >
                        {product.name}
                    </div>
                ))}
            </div>

            {popupVisible && (
                <div className="popup">
                    <div>
                        <h2>{foodData.name}</h2>
                        <p>Calories: {foodData.calories}</p>
                        <p>Carbs: {foodData.carbs}</p>
                        <p>Proteins: {foodData.proteins}</p>
                        <p>Fats: {foodData.fats}</p>
                    </div>
                    <button onClick={saveFood}>Save Food</button>
                    <button onClick={() => setPopupVisible(false)}>Close</button>
                </div>
            )}

            <div className="recently-added">
                <h3>Recently Added Foods:</h3>
                <ul>
                    {recentlyAddedFood.map((food, index) => (
                        <li key={index}>{food}</li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default LogFoodPage;
