import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import { Chart as ChartJS, LineElement, CategoryScale, Title, Tooltip, Legend } from "chart.js";

// Register Chart.js components
ChartJS.register(LineElement, CategoryScale, Title, Tooltip, Legend);

const SleepTracker = () => {
  const [sleepHours, setSleepHours] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [date, setDate] = useState(new Date().toLocaleDateString());
  const [graphData, setGraphData] = useState({
    labels: [],
    datasets: [
      {
        label: "Hours of Sleep",
        data: [],
        fill: false,
        borderColor: "#4B9CD3", // Stylish color palette
        tension: 0.1,
      },
    ],
  });

  const handleSleepChange = (e) => {
    setSleepHours(e.target.value);
  };

  const handleSleepSubmit = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.post("http://localhost:5000/api/sleep", {
        date: date,
        hours: sleepHours,
      });

      // Update graph with the new sleep data
      setGraphData((prevData) => {
        const newLabels = [...prevData.labels, date];
        const newData = [...prevData.datasets[0].data, sleepHours];
        return {
          labels: newLabels,
          datasets: [
            {
              ...prevData.datasets[0],
              data: newData,
            },
          ],
        };
      });
      setLoading(false);
    } catch (error) {
      setError("Error saving sleep data.");
      setLoading(false);
    }
  };

  return (
    <div className="sleep-tracker-container" style={{ padding: "20px", backgroundColor: "#f3f4f6" }}>
      <h2 style={{ color: "#4B9CD3" }}>Sleep Tracker</h2>
      <div className="sleep-input">
        <label htmlFor="sleepHours">Have you slept for 8 hours?</label>
        <input
          type="number"
          id="sleepHours"
          value={sleepHours || ""}
          onChange={handleSleepChange}
          min="0"
          max="24"
          style={{ padding: "10px", margin: "10px 0" }}
        />
        <button onClick={handleSleepSubmit} disabled={loading} style={{ padding: "10px 20px", backgroundColor: "#4B9CD3", color: "#fff" }}>
          {loading ? "Submitting..." : "Add Sleep"}
        </button>
      </div>

      {error && <div style={{ color: "red", marginTop: "10px" }}>{error}</div>}

      <div className="sleep-graph">
        <h3 style={{ color: "#4B9CD3" }}>Your Sleep Data</h3>
        <Line data={graphData} options={{ responsive: true, plugins: { title: { display: true, text: "Sleep Hours (last 7 days)" } } }} />
      </div>
    </div>
  );
};

export default SleepTracker;
