import React, { useState } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';

const Ashu = () => {
  const [image, setImage] = useState(null);
  const [ingredients, setIngredients] = useState([]);
  const [probabilities, setProbabilities] = useState([]);
  const [mass, setMass] = useState(100);  // Initialize with default mass value
  const [nutritionInfo, setNutritionInfo] = useState(null);
  const [capturing, setCapturing] = useState(false);

  const webcamRef = React.useRef(null);

  const captureImage = () => {
    setCapturing(true);
    const imageSrc = webcamRef.current.getScreenshot();  // Get base64 image string
    setImage(imageSrc);  // Set the image to the state

    // Check if imageSrc is valid before proceeding
    if (imageSrc) {
      console.log("Captured Image: ", imageSrc);  // Debugging log to verify the image
    } else {
      console.log("No image captured");
    }
  };

  const handleMassChange = (e) => {
    setMass(e.target.value);
  };

  // Handle base64 to send to backend for ingredient prediction
  const predictIngredients = async () => {
    try {
      const response = await axios.post('http://localhost:5001/predict_image', {
        image: image,  // Send the base64 image directly to the backend
        mass: mass     // Send the mass value along with the image
      });
      
      setIngredients(response.data.ingredients);
      setProbabilities(response.data.probabilities);
    } catch (error) {
      console.error("Error in image prediction:", error);
    }
  };

  const predictNutrition = async () => {
    try {
      const response = await axios.post('http://localhost:5001/predict_portion', {
        image: image,  // Send the base64 image directly to the backend
        mass: mass     // Send the mass value along with the image
      });
      
      setNutritionInfo(response.data);
    } catch (error) {
      console.error("Error in portion prediction:", error);
    }
  };

  return (
    <div className="App">
      <h1>Food Nutrient Prediction</h1>

      {capturing ? (
        <>
          <img src={image} alt="Captured" />
          <div>
            <label>Mass (grams):</label>
            <input 
              type="number" 
              value={mass} 
              onChange={handleMassChange}  // Ensure mass is updated on change
            />
            <button onClick={predictNutrition}>Predict Nutrition</button>
            <div>
              {nutritionInfo && (
                <div>
                  <h3>Nutrition Info:</h3>
                  <p>Protein: {nutritionInfo.protein}</p>
                  <p>Fat: {nutritionInfo.fat}</p>
                  <p>Carbs: {nutritionInfo.carbs}</p>
                  <p>Calories: {nutritionInfo.calories}</p>
                </div>
              )}
            </div>
            <div>
        <button onClick={predictIngredients}>Predict Ingredients</button>
        {ingredients.length > 0 ? (
          <ul>
            {ingredients.map((ingredient, index) => (
              <li key={index}>
                {ingredient}: {probabilities[index].toFixed(2)}% {/* Displaying probability as percentage */}
              </li>
            ))}
          </ul>
        ) : (
          <p>No ingredients predicted yet.</p>
        )}
      </div>
          </div>
        </>
      ) : (
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          width="100%"
        />
      )}

      <button onClick={captureImage}>Capture Image</button>

      
    </div>
  );
};

export default Ashu;
