import React, { useState, useEffect } from 'react';
; // Ensure your CSS is in the log.css file
import { Pie } from 'react-chartjs-2'; // Pie chart library for visualization
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';

ChartJS.register(ArcElement, Tooltip, Legend);

const LogFoodPage = () => {
    const [foodInput, setFoodInput] = useState('');
    const [quantity, setQuantity] = useState(100);
    const [unit, setUnit] = useState(1);
    const [suggestions, setSuggestions] = useState([]);
    const [foodData, setFoodData] = useState({});
    const [popupVisible, setPopupVisible] = useState(false);
    const [totalCalories, setTotalCalories] = useState(0);
    const [userProfile, setUserProfile] = useState({});
    const [mealType, setMealType] = useState('breakfast'); // Meal type selection

    // Fetch total calorie intake from user's profile
    useEffect(() => {
        fetchProfileData();
        fetchTotalCalories();
    }, []);

    const fetchProfileData = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5000/api/users/profile', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });
            const data = await response.json();
            setUserProfile(data);
        } catch (error) {
            console.error("Error fetching profile:", error);
        }
    };

    const fetchTotalCalories = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5001/user-calories', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });
            const data = await response.json();
            setTotalCalories(data.totalCalories);
        } catch (error) {
            console.error("Error fetching total calories:", error);
        }
    };

    const handleSearchChange = async (e) => {
        const searchTerm = e.target.value.trim();
        setFoodInput(searchTerm);

        if (searchTerm === "") {
            setSuggestions([]);
            return;
        }

        try {
            const response = await fetch(`http://localhost:5001/search?query=${searchTerm}`);
            const data = await response.json();
            setSuggestions(data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    const getProduct = async (code) => {
        const response = await fetch(`http://localhost:5001/product/${code}`);
        const data = await response.json();

        const adjustedQuantity = quantity || 100; 

        setFoodData({
            ...data,
            calories: (data.calories * adjustedQuantity) / 100,
            carbs: (data.carbs * adjustedQuantity) / 100,
            proteins: (data.proteins * adjustedQuantity) / 100,
            fats: (data.fats * adjustedQuantity) / 100,
        });

        setSuggestions([]);
        setPopupVisible(true);
    };

    const closePopup = () => {
        setPopupVisible(false);
        setFoodInput('');
    };

    const saveData = async () => {
        const token = localStorage.getItem('token');
        const data = {
            code: foodData.code,
            name: foodData.name,
            calories: foodData.calories,
            carbs: foodData.carbs,
            proteins: foodData.proteins,
            fats: foodData.fats,
            mealType: mealType, // Save under the selected meal type
        };

        const response = await fetch('http://localhost:5001/save', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`,
            },
            body: JSON.stringify(data),
        });

        if (response.ok) {
            alert('Food saved successfully');
            fetchTotalCalories();
            setSuggestions([]);
            closePopup();
        }
    };

    const chartData = {
        labels: ['Fats', 'Proteins', 'Carbs'],
        datasets: [
            {
                data: [foodData.fats, foodData.proteins, foodData.carbs],
                backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56'],
            },
        ],
    };

    return (
        <div className="log-food-page">
            <h1>Search for a Food Product</h1>
            <p>Total Calories: {totalCalories}</p>

            {/* Meal Type Selection */}
            <div className="meal-selector">
                <button onClick={() => setMealType('breakfast')}>Breakfast</button>
                <button onClick={() => setMealType('lunch')}>Lunch</button>
                <button onClick={() => setMealType('dinner')}>Dinner</button>
            </div>

            <div className="search-container">
                <input
                    type="text"
                    value={foodInput}
                    onChange={handleSearchChange}
                    placeholder="Enter food name"
                />
                <input
                    type="number"
                    value={quantity}
                    onChange={(e) => setQuantity(Number(e.target.value))}
                    placeholder="100"
                />
                <select onChange={(e) => setUnit(Number(e.target.value))} value={unit}>
                    <option value={1}>gm</option>
                    <option value={0.001}>mg</option>
                    <option value={1000}>kg</option>
                </select>
            </div>

            <div id="suggestions" className="suggestions">
                {suggestions.length > 0 ? (
                    suggestions.map((product) => (
                        <div key={product.code} onClick={() => getProduct(product.code)}>
                            <strong>{product.name}</strong>
                        </div>
                    ))
                ) : (
                    <p>No suggestions available</p>
                )}
            </div>

            {popupVisible && (
                <div className="popup">
                    <div id="popupContent">
                        <h2>{foodData.name} ({quantity}g)</h2>
                        <p><strong>Calories:</strong> {foodData.calories.toFixed(2)} kcal</p>
                        <p><strong>Carbohydrates:</strong> {foodData.carbs.toFixed(2)} g</p>
                        <p><strong>Proteins:</strong> {foodData.proteins.toFixed(2)} g</p>
                        <p><strong>Fats:</strong> {foodData.fats.toFixed(2)} g</p>
                    </div>
                    <button onClick={saveData}>Save</button>
                    <button onClick={closePopup}>Close</button>
                </div>
            )}

            {/* Pie chart displaying calories distribution */}
            <div className="calories-chart">
                <h3>Calorie Distribution</h3>
                <Pie data={chartData} />
            </div>

            {/* Recent and favorite foods section */}
            <div className="recent-favorite">
                <div className="nutrition-info">
                    <h3>Nutrition Info</h3>
                    <Pie data={chartData} />
                </div>
                <div className="recently-added">
                    <h3>Recently Added</h3>
                    <p>The recently added food will appear here</p>
                </div>
                <div className="favorite-food">
                    <h3>Most Favorite</h3>
                    <p>Add your most favorite food</p>
                </div>
            </div>
        </div>
    );
};

export default LogFoodPage;
