import React, { useState, useEffect } from 'react';
import './log.css'; // Ensure your CSS is in the log.css file

const LogFoodPage = () => {
    const [foodInput, setFoodInput] = useState('');
    const [quantity, setQuantity] = useState(100);
    const [unit, setUnit] = useState(1);
    const [suggestions, setSuggestions] = useState([]);
    const [foodData, setFoodData] = useState({});
    const [popupVisible, setPopupVisible] = useState(false);

    // Fetch total calorie intake on page load
    useEffect(() => {
        fetch('/user-calories')
            .then((response) => response.json())
            .then((data) => {
                // Display total calories when fetched
                console.log("Total calories:", data.totalCalories);
            })
            .catch((error) => console.error("Error fetching total calories:", error));
    }, []);

    // Handle search input change and fetch suggestions
    const handleSearchChange = async (e) => {
        const searchTerm = e.target.value;
        setFoodInput(searchTerm);

        if (!searchTerm.trim()) {
            setSuggestions([]);
            return;
        }

        const response = await fetch(`/search?query=${searchTerm}`);
        const data = await response.json();
        setSuggestions(data);
    };

    // Fetch product details and show in popup
    const getProduct = async (code) => {
        const response = await fetch(`/product/${code}`);
        const data = await response.json();
        
        const adjustedQuantity = quantity || 100; // Default to 100 if quantity is not provided
        
        setFoodData({
            ...data,
            calories: (data.calories * adjustedQuantity) / 100,
            carbs: (data.carbs * adjustedQuantity) / 100,
            proteins: (data.proteins * adjustedQuantity) / 100,
            fats: (data.fats * adjustedQuantity) / 100
        });

        setPopupVisible(true);
    };

    // Close popup
    const closePopup = () => {
        setPopupVisible(false);
    };

    // Save food data to the database and update user calorie intake
    const saveData = async () => {
        const data = {
            code: foodData.code,
            name: foodData.name,
            calories: foodData.calories,
            carbs: foodData.carbs,
            proteins: foodData.proteins,
            fats: foodData.fats
        };

        const response = await fetch('/save', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(data)
        });

        if (response.ok) {
            alert('Food saved successfully');
            // After saving food, update user total calories
            const calorieResponse = await fetch('/user-calories');
            const calorieData = await calorieResponse.json();
            console.log("Updated total calories:", calorieData.totalCalories);
            setFoodInput('');
            closePopup();
        }
    };

    return (
        <div className="log-food-page">
            <h1>Search for a Food Product</h1>

            <div className="search-container">
                <input
                    type="text"
                    value={foodInput}
                    onChange={handleSearchChange}
                    placeholder="Enter food name"
                />
                <input
                    type="number"
                    value={quantity}
                    onChange={(e) => setQuantity(Number(e.target.value))}
                    placeholder="100"
                />
                <select onChange={(e) => setUnit(Number(e.target.value))} value={unit}>
                    <option value={1}>gm</option>
                    <option value={0.001}>mg</option>
                    <option value={1000}>kg</option>
                </select>
            </div>

            <div id="suggestions" className="suggestions">
                {suggestions.map((product) => (
                    <div key={product.code} onClick={() => getProduct(product.code)}>
                        <strong>{product.name}</strong>
                    </div>
                ))}
            </div>

            {popupVisible && (
                <div className="popup">
                    <div id="popupContent">
                        <h2>{foodData.name} ({quantity}g)</h2>
                        <p><strong>Calories:</strong> {foodData.calories.toFixed(2)} kcal</p>
                        <p><strong>Carbohydrates:</strong> {foodData.carbs.toFixed(2)} g</p>
                        <p><strong>Proteins:</strong> {foodData.proteins.toFixed(2)} g</p>
                        <p><strong>Fats:</strong> {foodData.fats.toFixed(2)} g</p>
                    </div>
                    <button onClick={saveData}>Save</button>
                    <button onClick={closePopup}>Close</button>
                </div>
            )}
        </div>
    );
};

export default LogFoodPage;
