import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Bar } from "react-chartjs-2";
// Ensure the image is placed correctlyimport "./Progress.css";

const Progress = () => {
  const [userData, setUserData] = useState(null);
  const [bmiCategory, setBmiCategory] = useState("");
  const [bmiColor, setBmiColor] = useState("#fff");
  const [bodyMeasurements, setBodyMeasurements] = useState({
    chest: "",
    waist: "",
    hips: "",
  });
  const [showUpdatePopup, setShowUpdatePopup] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }
    fetchUserData(token);
  }, [navigate]);

  const fetchUserData = async (token) => {
    try {
      const response = await axios.get("http://localhost:5000/api/users/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.status === 200) {
        setUserData(response.data);
        calculateBMI(response.data);
      } else {
        console.error("User data fetch failed");
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  const calculateBMI = (data) => {
    if (data?.weight && data?.height) {
      let heightInMeters = data.height / 100;
      let bmi = (data.weight / (heightInMeters * heightInMeters)).toFixed(1);
      let category = "";
      let color = "";

      if (bmi < 18.5) {
        category = "Underweight";
        color = "#3498db";
      } else if (bmi >= 18.5 && bmi < 24.9) {
        category = "Healthy Weight";
        color = "#2ecc71";
      } else if (bmi >= 25 && bmi < 29.9) {
        category = "Overweight";
        color = "#f39c12";
      } else {
        category = "Obese";
        color = "#e74c3c";
      }

      setBmiCategory(category);
      setBmiColor(color);
    }
  };

  const handleUpdateMeasurements = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "http://localhost:5000/api/users/update-measurements",
        { ...bodyMeasurements },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Measurements updated successfully!");
      setShowUpdatePopup(false);
    } catch (error) {
      console.error("Error updating measurements:", error);
    }
  };

  const weightProgressData = {
    labels: ["Week 1", "Week 2", "Week 3", "Week 4"],
    datasets: [
      {
        label: "Weight Progress",
        data: [userData?.weight || 70, 69, 68, 67],
        backgroundColor: "#1E90FF",
      },
    ],
  };

  return (
    <div className="progress-container">
      <h1 className="title">Your Progress</h1>

      {/* ✅ BMI Section */}
      <div className="progress-box bmi-box">
        <h2>BMI & Body Category</h2>
        <div className="bmi-display">
          <div className="bmi-circle" style={{ backgroundColor: bmiColor }}>
            <span>{bmiCategory}</span>
          </div>
          <div className="bmi-ranges">
            <p><span className="underweight">Underweight:</span> BMI {"<"} 18.5</p>
            <p><span className="normal">Healthy Weight:</span> 18.5 - 24.9</p>
            <p><span className="overweight">Overweight:</span> 25 - 29.9</p>
            <p><span className="obese">Obese:</span> BMI {">="} 30</p>
          </div>
        </div>
      </div>

      {/* ✅ Body Measurements */}
      <div className="progress-box measurements-box">
        <h2>Body Measurements</h2>
        <p>Height: {userData?.height} cm</p>
        <p>Weight: {userData?.weight} kg</p>
        <p>Chest: {bodyMeasurements.chest || "--"} cm</p>
        <p>Waist: {bodyMeasurements.waist || "--"} cm</p>
        <p>Hips: {bodyMeasurements.hips || "--"} cm</p>
        <button className="update-btn" onClick={() => setShowUpdatePopup(true)}>Update Details</button>
      </div>

      {/* ✅ Goal Weight Visualization */}
      <div className="progress-box goal-weight-box">
        <h2>Goal Weight: {userData?.targetWeight} kg</h2>
        <div className="goal-visual">
          <p>{userData?.estimatedTimeToTargetWeight} weeks to goal</p>
        </div>
      </div>

      {/* ✅ Weight Progress Graph */}
      <div className="progress-box weight-box">
        <h2>Weight Progress</h2>
        <Bar data={weightProgressData} />
      </div>

      {/* ✅ View Statistics */}
      <div className="progress-box statistics-box">
        <h2>Daily Health Stats</h2>
        <button className="stats-btn">View Statistics</button>
      </div>
    </div>
  );
};

export default Progress;
