import React, { useState, useEffect } from 'react';
import './log.css'; // Ensure your CSS is in the log.css file
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { ProgressBar } from 'react-bootstrap';

ChartJS.register(ArcElement, Tooltip, Legend);

const LogFoodPage = () => {
    const [foodInput, setFoodInput] = useState('');
    const [quantity, setQuantity] = useState(100);
    const [unit, setUnit] = useState(1);
    const [suggestions, setSuggestions] = useState([]);
    const [foodData, setFoodData] = useState({});
    const [popupVisible, setPopupVisible] = useState(false);
    const [totalCalories, setTotalCalories] = useState(0);
    const [mealType, setMealType] = useState('breakfast');
    const [userProfile, setUserProfile] = useState({});
    
    // Fetch total calorie intake from the backend
    useEffect(() => {
        fetchTotalCalories();
        fetchUserProfile();
    }, [mealType]);

    // Fetch total calories from the backend
    const fetchTotalCalories = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5001/user-calories', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });
            const data = await response.json();
            setTotalCalories(data.totalCalories);
        } catch (error) {
            console.error("Error fetching total calories:", error);
        }
    };

    // Fetch user profile to get recommended calorie intake
    const fetchUserProfile = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5000/api/users/profile', {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });
            const data = await response.json();
            setUserProfile(data);
        } catch (error) {
            console.error("Error fetching user profile:", error);
        }
    };

    // Handle search input change and fetch suggestions
    const handleSearchChange = async (e) => {
        const searchTerm = e.target.value.trim();
        setFoodInput(searchTerm);

        if (searchTerm === "") {
            setSuggestions([]);
            return;
        }

        try {
            const response = await fetch(`http://localhost:5001/search?query=${searchTerm}`);
            const data = await response.json();
            setSuggestions(data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    };

    // Fetch product details and show in popup
    const getProduct = async (code) => {
        const response = await fetch(`http://localhost:5001/product/${code}`);
        const data = await response.json();
        const adjustedQuantity = quantity || 100; // Default to 100 if quantity is not provided
        setFoodData({
            ...data,
            calories: (data.calories * adjustedQuantity) / 100,
            carbs: (data.carbs * adjustedQuantity) / 100,
            proteins: (data.proteins * adjustedQuantity) / 100,
            fats: (data.fats * adjustedQuantity) / 100,
        });
        setSuggestions([]);
        setPopupVisible(true);
    };

    // Close popup
    const closePopup = () => {
        setPopupVisible(false);
        setFoodInput('');
    };

    // Save food data to the database and update user calorie intake
    const saveData = async () => {
        const token = localStorage.getItem('token');
        const data = {
            code: foodData.code,
            name: foodData.name,
            calories: foodData.calories,
            carbs: foodData.carbs,
            proteins: foodData.proteins,
            fats: foodData.fats,
            mealType,
        };

        const response = await fetch('http://localhost:5001/save', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`,
            },
            body: JSON.stringify(data),
        });

        if (response.ok) {
            alert('Food saved successfully');
            fetchTotalCalories(); // Refresh the total calorie count
            setSuggestions([]);
            closePopup();
        }
    };

    // Chart data for macro nutrients
    const pieChartData = {
        labels: ['Carbs', 'Proteins', 'Fats'],
        datasets: [
            {
                label: 'Macro Nutrients',
                data: [foodData.carbs, foodData.proteins, foodData.fats],
                backgroundColor: ['#FF6347', '#4CAF50', '#FFD700'],
                borderColor: ['#FF6347', '#4CAF50', '#FFD700'],
                borderWidth: 1,
            },
        ],
    };

    // Calculate progress circle
    const progressPercentage = (totalCalories / userProfile.calorieIntake) * 100;

    return (
        <div className="log-food-page">
            <h1>Search for a Food Product</h1>
            <p>Total Calories: {totalCalories}</p>

            <div className="search-container">
                <input
                    type="text"
                    value={foodInput}
                    onChange={handleSearchChange}
                    placeholder="Enter food name"
                />
                <input
                    type="number"
                    value={quantity}
                    onChange={(e) => setQuantity(Number(e.target.value))}
                    placeholder="100"
                />
                <select onChange={(e) => setUnit(Number(e.target.value))} value={unit}>
                    <option value={1}>gm</option>
                    <option value={0.001}>mg</option>
                    <option value={1000}>kg</option>
                </select>
            </div>

            <div id="suggestions" className="suggestions">
                {suggestions.length > 0 ? (
                    suggestions.map((product) => (
                        <div key={product.code} onClick={() => getProduct(product.code)}>
                            <strong>{product.name}</strong>
                        </div>
                    ))
                ) : (
                    <p>No suggestions available</p>
                )}
            </div>

            {popupVisible && (
                <div className="popup">
                    <div id="popupContent">
                        <h2>{foodData.name} ({quantity}g)</h2>
                        <p><strong>Calories:</strong> {foodData.calories.toFixed(2)} kcal</p>
                        <p><strong>Carbohydrates:</strong> {foodData.carbs.toFixed(2)} g</p>
                        <p><strong>Proteins:</strong> {foodData.proteins.toFixed(2)} g</p>
                        <p><strong>Fats:</strong> {foodData.fats.toFixed(2)} g</p>
                        <select
                            onChange={(e) => setMealType(e.target.value)}
                            value={mealType}
                        >
                            <option value="breakfast">Breakfast</option>
                            <option value="lunch">Lunch</option>
                            <option value="dinner">Dinner</option>
                        </select>
                    </div>
                    <button onClick={saveData}>Save</button>
                    <button onClick={closePopup}>Close</button>
                </div>
            )}

            {/* Total Calorie Intake Section */}
            <div className="total-calorie-intake">
                <div className="circle-progress">
                    <div className="circle" style={{ width: `${progressPercentage}%`, height: `${progressPercentage}%` }} />
                </div>
                <p>Total Calorie Intake: {userProfile.calorieIntake}</p>
                <Pie data={pieChartData} />
            </div>
        </div>
    );
};

export default LogFoodPage;
