import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Home.css";
import { FaPlus, FaMinus, FaGlassWhiskey } from "react-icons/fa"; // Water glass icon
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

const Home = () => {
  const [userData, setUserData] = useState(null);
  const [waterIntake, setWaterIntake] = useState(0);
  const [temperature, setTemperature] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchUserData();
    fetchWeatherData();
  }, []);

  // ✅ Fetch user data after login
  const fetchUserData = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await fetch("http://localhost:5000/api/users/me", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const data = await response.json();
      if (response.ok) {
        setUserData(data);
      } else {
        console.error("User data fetch failed:", data);
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  // ✅ Fetch weather data (temperature)
  const fetchWeatherData = async () => {
    try {
      const response = await fetch(
        `https://api.weatherapi.com/v1/current.json?key=YOUR_API_KEY&q=auto:ip`
      );
      const data = await response.json();
      if (response.ok) {
        setTemperature(data.current.temp_c);
      }
    } catch (error) {
      console.error("Error fetching weather data:", error);
    }
  };

  // ✅ Handle Water Intake (Max: 7 Glasses)
  const handleWaterChange = (change) => {
    setWaterIntake((prev) => Math.max(0, Math.min(7, prev + change)));
  };

  // ✅ Calculate Water Intake Percentage
  const waterPercentage = (waterIntake / 7) * 100;

  return (
    <div className="home-container">
      <header className="header">
        <h1>Nuvola</h1>
        <nav>
          <ul>
            <li onClick={() => navigate("/goals")}>Goals</li>
            <li onClick={() => navigate("/progress")}>Progress</li>
            <li onClick={() => navigate("/dietplan")}>Diet Plan</li>
            <li onClick={() => navigate("/foodstore")}>Food Store</li>
          </ul>
        </nav>
        <button className="profile-btn" onClick={() => navigate("/profile")}>
          Profile
        </button>
      </header>

      <div className="dashboard">
        {/* ✅ Water Intake Section */}
        <div className="water-intake">
          <h2>Water Intake</h2>
          <p>Today's Climate: {temperature ? `${temperature}°C` : "Loading..."}</p>
          <div className="water-tracker">
            <button onClick={() => handleWaterChange(-1)}>
              <FaMinus />
            </button>
            <div className="glass">
              <FaGlassWhiskey className="glass-icon" />
              <div
                className="water-fill"
                style={{ height: `${waterPercentage}%` }}
              ></div>
            </div>
            <button onClick={() => handleWaterChange(1)}>
              <FaPlus />
            </button>
          </div>
          <p>{waterIntake}/7 Glasses</p>
        </div>

        {/* ✅ Track Food Section */}
        <div className="track-food">
          <h2>Track Food</h2>
          <div className="calorie-circle">
            <CircularProgressbar
              value={0}
              maxValue={userData?.calorieIntake || 2000}
              text={`0 / ${userData?.calorieIntake || "N/A"} kcal`}
              styles={buildStyles({
                textColor: "#333",
                pathColor: "#ff5733",
                trailColor: "#ddd",
              })}
            />
          </div>
          <div className="macros">
            <div className="macro-bar protein">
              <span>Protein</span>
              <div style={{ width: `${userData?.macros?.protein / 3}px` }}></div>
            </div>
            <div className="macro-bar carbs">
              <span>Carbs</span>
              <div style={{ width: `${userData?.macros?.carbs / 3}px` }}></div>
            </div>
            <div className="macro-bar fats">
              <span>Fats</span>
              <div style={{ width: `${userData?.macros?.fats / 3}px` }}></div>
            </div>
            <div className="macro-bar magnesium">
              <span>Magnesium</span>
              <div style={{ width: "80px" }}></div>
            </div>
            <div className="macro-bar sodium">
              <span>Sodium</span>
              <div style={{ width: "120px" }}></div>
            </div>
          </div>
        </div>
      </div>

      {/* ✅ Food Logging Section */}
      <div className="food-logging">
        {["Breakfast", "Lunch", "Dinner", "Snack"].map((meal) => (
          <div key={meal} className="meal-log">
            <p>Recommended: 300-500 cal</p>
            <button>{`Log ${meal}`}</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
