import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Home.css";
import { FaPlus, FaMinus, FaGlassWhiskey } from "react-icons/fa";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";
import { PieChart } from 'react-minimal-pie-chart'; // Pie chart library

const Home = () => {
  const [userData, setUserData] = useState(null);
  const [waterIntake, setWaterIntake] = useState(0);
  const [temperature, setTemperature] = useState(null);
  const [mealType, setMealType] = useState("breakfast");
  const [suggestions, setSuggestions] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      console.log("User not logged in. Redirecting...");
      navigate("/login"); // Redirect to login if no token
      return;
    }

    fetchUserData(token);
    fetchWeatherData();
  }, [navigate]);

  // ✅ Fetch user data after login
  const fetchUserData = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/users/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const data = await response.json();
      if (response.ok) {
        setUserData(data);
        console.log("User data loaded:", data);
      } else {
        console.error("User data fetch failed:", data);
      }
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  // ✅ Fetch weather data (temperature)
  const fetchWeatherData = async () => {
    try {
      const response = await fetch(
        `https://api.weatherapi.com/v1/current.json?key=YOUR_API_KEY&q=auto:ip`
      );
      const data = await response.json();
      if (response.ok) {
        setTemperature(data.current.temp_c);
        console.log("Weather data loaded:", data);
      }
    } catch (error) {
      console.error("Error fetching weather data:", error);
    }
  };

  // ✅ Handle Water Intake (Max: 7 Glasses)
  const handleWaterChange = (change) => {
    setWaterIntake((prev) => Math.max(0, Math.min(7, prev + change)));
  };

  // ✅ Handle Search Input Change and Suggestions
  const handleSearchChange = async (e) => {
    const searchTerm = e.target.value.trim();
    setFoodInput(searchTerm);

    if (searchTerm === "") {
      setSuggestions([]);
      return;
    }

    try {
      const response = await fetch(`http://localhost:5001/search?query=${searchTerm}`);
      const data = await response.json();
      setSuggestions(data);
    } catch (error) {
      console.error("Error fetching suggestions:", error);
    }
  };

  // ✅ Handle Product Selection and Pop-Up
  const getProduct = async (code) => {
    const response = await fetch(`http://localhost:5001/product/${code}`);
    const data = await response.json();
    
    const adjustedQuantity = quantity || 100;

    setFoodData({
      ...data,
      calories: (data.calories * adjustedQuantity) / 100,
      carbs: (data.carbs * adjustedQuantity) / 100,
      proteins: (data.proteins * adjustedQuantity) / 100,
      fats: (data.fats * adjustedQuantity) / 100,
    });

    setSuggestions([]);
    setPopupVisible(true);
  };

  // ✅ Close Pop-Up
  const closePopup = () => {
    setPopupVisible(false);
    setFoodInput('');
  };

  // ✅ Save Food Data
  const saveData = async () => {
    const token = localStorage.getItem('token');
    const data = {
      code: foodData.code,
      name: foodData.name,
      calories: foodData.calories,
      carbs: foodData.carbs,
      proteins: foodData.proteins,
      fats: foodData.fats,
      mealType: mealType,
    };

    const response = await fetch('http://localhost:5001/save', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify(data),
    });

    if (response.ok) {
      alert('Food saved successfully');
      fetchTotalCalories();
      setSuggestions([]);
      closePopup();
    }
  };

  return (
    <div className="home-container">
      <header className="header">
        <h1>Nuvola</h1>
        <nav>
          <ul>
            <li onClick={() => navigate("/goals")}>goals</li>
            <li onClick={() => navigate("/progress")}>Progress</li>
            <li onClick={() => navigate("/dietplan")}>Diet Plan</li>
            <li onClick={() => window.location.href = "https://nuv0la.online"}>Food Store</li>
          </ul>
        </nav>
        <button className="profile-btn" onClick={() => navigate("/profile")}>P</button>
      </header>

      {/* Total Calorie Intake Section */}
      <div className="total-calorie-intake">
        <h2>Total Calorie Intake ({mealType})</h2>
        <div className="circle-progress">
          <div className="circle" style={{ width: `${(totalCalories / recommendedCalories) * 100}%`, height: `${(totalCalories / recommendedCalories) * 100}%` }}></div>
        </div>
        <p>Logged: {totalCalories} / {recommendedCalories} kcal</p>
        {/* Pie chart for Macro Nutrients */}
        <PieChart
          data={[
            { title: 'Protein', value: userData?.macros?.protein || 0, color: '#FF5733' },
            { title: 'Carbs', value: userData?.macros?.carbs || 0, color: '#FFCD33' },
            { title: 'Fats', value: userData?.macros?.fats || 0, color: '#33FF57' },
          ]}
        />
      </div>

      {/* Water Intake Section */}
      <div className="water-intake">
        <h2>Water Intake</h2>
        <p className="climate">{temperature ? `${temperature}°C` : "Loading..."}</p>
        <div className="water-circle">
          <div className="water-fill" style={{ height: `${(waterIntake / 7) * 100}%` }}></div>
          <FaGlassWhiskey className="glass-icon" />
        </div>
        <div className="water-tracker">
          <button onClick={() => handleWaterChange(-1)}><FaMinus /></button>
          <p>{waterIntake}/7 Glasses</p>
          <button onClick={() => handleWaterChange(1)}><FaPlus /></button>
        </div>
      </div>

      {/* Food Search and Logging Section */}
      <div className="food-logging">
        <input
          type="text"
          value={foodInput}
          onChange={handleSearchChange}
          placeholder="Enter food name"
        />
        {/* Suggestions will appear here */}
        {suggestions.length > 0 && (
          <div className="suggestions">
            {suggestions.map((product) => (
              <div key={product.code} onClick={() => getProduct(product.code)}>
                <strong>{product.name}</strong>
              </div>
            ))}
          </div>
        )}
        <button onClick={() => saveData()}>Save Food</button>
      </div>
    </div>
  );
};

export default Home;
